package com.example.sportsfat.data.repositoryImpl

import com.example.sportsfat.R
import com.example.sportsfat.data.database.dao.DAO
import com.example.sportsfat.data.database.entity.workouts.listWorkouts.WorkoutEntity
import com.example.sportsfat.data.database.entity.workouts.mondayWorkouts.MondayWorkoutsEntity
import com.example.sportsfat.domain.model.workout.WorkoutModel
import com.example.sportsfat.domain.model.workout.mondayWorkout.MondayWorkoutModel
import com.example.sportsfat.domain.workouts.WorkoutsRepository
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import kotlinx.coroutines.withContext
import java.util.*
import javax.inject.Inject

class WorkoutsRepositoryImpl @Inject constructor(
    private val DAO: DAO
) : WorkoutsRepository {

    override suspend fun getData() {
        return withContext(Dispatchers.IO) {
            if (!DAO.doesWorkoutsEntityExist()) {
                val workouts = getWorkouts()
                workouts.map { workoutsList ->
                    val workoutsEntity =
                        WorkoutEntity(
                            Random().nextInt(),
                            workoutsList.name,
                            workoutsList.description,
                            workoutsList.implementationOptions,
                            workoutsList.executionTechnique,
                            workoutsList.image,
                            workoutsList.keyWorkout,
                            workoutsList.approaches,
                            workoutsList.repetitions,
                            workoutsList.weight
                        )
                    DAO.insertWorkoutsEntity(workoutsEntity)
                }
            }
        }
    }

    override suspend fun showData(): Flow<List<WorkoutModel>> {
        return withContext(Dispatchers.IO) {
            val workoutsEntity = DAO.getWorkoutsEntities()
            workoutsEntity.map { workoutsList ->
                workoutsList.map {
                    WorkoutModel(
                        it.name,
                        it.description,
                        it.implementationOptions,
                        it.executionTechnique,
                        it.image,
                        it.keyWorkout,
                        it.approaches,
                        it.repetitions,
                        it.weight,
                        isFavorite = false
                    )
                }
            }
        }
    }

    override suspend fun addMondayWorkouts(
        workoutModel: WorkoutModel,
        isFavorite: Boolean
    ) {
        return withContext(Dispatchers.IO) {
            DAO.insertMondayWorkoutsEntity(
                MondayWorkoutsEntity(
                    Random().nextInt(),
                    workoutModel.name,
                    workoutModel.description,
                    workoutModel.implementationOptions,
                    workoutModel.executionTechnique,
                    workoutModel.image,
                    workoutModel.keyWorkout,
                    workoutModel.approaches,
                    workoutModel.repetitions,
                    workoutModel.weight
                )
            )
            DAO.addToWorkouts(workoutModel.name, isFavorite)
        }
    }

    override suspend fun getMondayWorkouts(): Flow<List<MondayWorkoutModel>> {
        return withContext(Dispatchers.IO) {
            val mondayWorkoutsEntity = DAO.getMondayWorkoutsEntity()
            mondayWorkoutsEntity.map { workoutsList ->
                workoutsList.map {
                    MondayWorkoutModel(
                        it.name,
                        it.description,
                        it.implementationOptions,
                        it.executionTechnique,
                        it.image,
                        it.keyWorkout,
                        it.approaches,
                        it.repetitions,
                        it.weight
                    )
                }
            }
        }
    }

    override suspend fun deleteWorkoutByName(name: String) {
        withContext(Dispatchers.IO) {
            DAO.deleteWorkoutEntityByName(name)
        }
    }

    override suspend fun findWorkoutEntityByName(searchText: String): WorkoutModel {
        return withContext(Dispatchers.IO) {
            val workoutsEntity = DAO.findWorkoutEntityByName(searchText)
            WorkoutModel(
                workoutsEntity.name,
                workoutsEntity.description,
                workoutsEntity.implementationOptions,
                workoutsEntity.executionTechnique,
                workoutsEntity.image,
                workoutsEntity.keyWorkout,
                workoutsEntity.approaches,
                workoutsEntity.repetitions,
                workoutsEntity.weight,
                isFavorite = false
            )
        }
    }

    override suspend fun saveApproaches(
        name: String,
        approaches: String,
        repetitions: String,
        weight: String
    ) {
        return withContext(Dispatchers.IO) {
            DAO.saveApproaches(name, approaches, repetitions, weight)
        }
    }

    fun getWorkouts(): List<WorkoutModel> {
        val listWorkout = listOf<WorkoutModel>(
            WorkoutModel(
                name = "Жим Лежа",
                description = "Является одной из основных дисциплин в пауэрлифтинге (наряду c приседанием и становой тягой), где используется отличная от бодибилдинга техника: с целью жима максимально большого веса напрягаются трицепсы, передние пучки дельтовидных мышц и широчайшие мышцы спины; роль грудных мышц значительно снижена.\n",
                implementationOptions = "В бодибилдинге варьирование техники жима лёжа позволяет акцентировать нагрузку на разных мышцах. При выполнении жима лёжа штанги узким хватом бо́льшая нагрузка ложится на трицепсы. Гриф можно опускать на верхнюю часть груди, среднюю и нижнюю, что позволяет предотвратить «застой» в росте мышц. Вариантами жима лёжа также являются жим на тренажёре Смита, жим гантелей, жим в наклоне и жим обратным хватом.\n" +
                        "\n" +
                        "Во время жима лёжа на горизонтальной скамье, в силу анатомических особенностей грудных мышц, большую часть нагрузки получают нижний отдел грудных мышц и трицепс, особенно, если используется \"мост\" как в пауэрлифтинге.\n" +
                        "При жиме лёжа на наклонной скамье вверх головой, в зависимости от угла наклона, нагрузка смещается с нижних отделов грудных мышц в сторону среднего и верхнего отдела, но при этом так же больше включаются в работу передние пучки дельтовидных мышц.\n" +
                        "При жиме на наклонной скамье вниз головой более акцентировано задействованы нижние отделы грудных и трицепс, передние пучки дельтовидных мышц при этом работают меньше.\n" +
                        "Жим гантелей позволяет опустить вес в нижней точке существенно ниже, так как гриф штанги позволяет опустить вес только до уровня груди. Кроме того, при жиме гантелей можно изменять траекторию движения, сводить гантели друг к другу в верхней точке, выжимать гантели, расположенные параллельно друг к другу, что позволяет задействовать новые пучки мышц и действует на них по-другому[1].\n" +
